sp_widget.client_script:
api.controller=function($timeout) {
	/* widget controller */
	var c = this


	$timeout(function() {


		var  mySwiper = new Swiper ('.swiper-container', {
			// Optional parameters
			direction: 'horizontal',
			loop: true,
			speed: 300,
			mousewheel: true,
			coverflowEffect: {
				rotate: 30,
				slideShadows: true
			},
			// If we need pagination
			pagination: {
				el: '.swiper-pagination',
			},

			// Navigation arrows
			navigation: {
				nextEl: '.swiper-button-next',
				prevEl: '.swiper-button-prev',
			},

			// And if we need scrollbar
			scrollbar: {
				el: '.swiper-scrollbar',
			},
		})

		}, 1000, false);



	c.data.product = {
		name: 'iPhone 15 Pro',
		price: '$999',
		images: [
			{ src: '10off.png', alt: 'Black Titanium iPhone' },
			{ src: '10on.png', alt: 'White Titanium iPhone' },
			{ src: '10startoff.png', alt: 'Blue Titanium iPhone' },
			// Add more images as needed
		],
		colors: [
			{ label: 'Natural Titanium', value: 'natural_titanium' },
			{ label: 'White Titanium', value: 'white_titanium' },
			{ label: 'Black Titanium', value: 'black_titanium' },
			// Add more colors as needed
		],
		capacities: [
			{ label: '128GB', value: '128' },
			{ label: '256GB', value: '256' },
			{ label: '512GB', value: '512' },
			// Add more capacities as needed
		],
		pricingPlans: [
			{ label: 'Fast Forward Pricing - 2 Year Voice & Data (+$3/month)', value: 'fast_forward' },
			{ label: 'Plus Pricing - 2 Year Voice & Data (+$29.17/month)', value: 'plus_pricing' },
			{ label: '2 Year Voice & Data', value: '2_year_voice_data' },
			{ label: 'Monthly or Prepaid', value: 'monthly_prepaid' },
			// Add more plans as needed
		]
	};

	c.data.selectedColor = c.data.product.colors[0].value;
	c.data.selectedCapacity = c.data.product.capacities[0].value;
	c.data.selectedPlan = c.data.product.pricingPlans[0].value;

	// Add functions for interaction if needed
};

